mapper.update:
UPDATE user_messages
SET is_read = 1
WHERE user_id = #{userId}
AND id IN

    #{id}  

AND is_read = 0  -- 仅标记未读消息

mapper.delete:
DELETE FROM user_messages
WHERE user_id = #{userId}
AND id IN

    #{id}

mapper.select:
SELECT COUNT(1) FROM user_messages
WHERE user_id = #{userId}
AND id IN

    #{id}

SELECT COUNT(1) FROM user_messages
WHERE user_id = #{userId}
  AND is_read = 0
